import { Component } from '@angular/core';
import { UiServiceService } from 'src/service/ui-service.service';  // ensure correct path

// Message interface
interface Message {
  sender: 'user' | 'bot';
  text: string;
  cached?: boolean;
  source?: string;
}

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  messages: Message[] = [];
  userInput: string = '';
  loading: boolean = false;

  constructor(private chatService: UiServiceService) {}

  sendMessage() {
    const query = this.userInput.trim();
    if (!query) return;

    // Push user message to chat window
    this.messages.push({ sender: 'user', text: query });
    this.userInput = '';
    this.loading = true;

    // Send to FastAPI backend
    this.chatService.requestChat({ message: query }).subscribe({
      next: (res) => {
        this.messages.push({
          sender: 'bot',
          text: res.reply || 'No response received.',
          cached: res.cached,
          source: res.source
        });
        this.loading = false;
      },
      error: (err) => {
        console.error(err);
        this.messages.push({
          sender: 'bot',
          text: '‚ö†Ô∏è Error contacting the server.'
        });
        this.loading = false;
      }
    });
  }
}

<div class="chat-container">
  <div class="chat-window">
    <div *ngFor="let msg of messages" [ngClass]="msg.sender">
      <div class="bubble">
        <strong *ngIf="msg.sender === 'bot'">ü§ñ</strong>
        <strong *ngIf="msg.sender === 'user'">üßë</strong>
        {{ msg.text }}
        <small *ngIf="msg.cached" class="cached-tag">(cached)</small>
      </div>
    </div>

    <div *ngIf="loading" class="loading">
      <span class="dot"></span><span class="dot"></span><span class="dot"></span>
    </div>
  </div>

  <form (submit)="sendMessage()" class="input-box">
    <input
      type="text"
      [(ngModel)]="userInput"
      name="message"
      placeholder="Type your question..."
      autocomplete="off"
      required
    />
    <button type="submit" [disabled]="loading">Send</button>
  </form>
</div>
